// WordMapChart.tsx
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import MarkerClusterGroup from 'react-leaflet-markercluster';

interface WordMapChartProps {
  data: { lat: number; lng: number; word: string }[];
}

const WordMapChart: React.FC<WordMapChartProps> = ({ data }) => {
  const center = { lat: 0, lng: 0 }; // Set your initial map center

  return (
    <MapContainer center={center} zoom={2} style={{ height: '500px', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />

      <MarkerClusterGroup>
        {data.map(({ lat, lng, word }, index) => (
          <Marker key={index} position={{ lat, lng }}>
            <Popup>{word}</Popup>
          </Marker>
        ))}
      </MarkerClusterGroup>
    </MapContainer>
  );
};

export default WordMapChart;
