import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import Modal from 'react-modal';

Modal.setAppElement('#root');

const ActionButton = ({id, sourceName}) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };
  return (
    <td>
        <Link to={`/admin/mera-bazzar/edit-${sourceName}/${id}`} className="action-icon"> <i className="mdi mdi-square-edit-outline"></i></Link>
        <Link onClick={openModal} className="action-icon"> <i className="mdi mdi-delete"></i></Link>

        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          contentLabel="Example Modal"
        >
          <h2>Modal Title</h2>
          <p>Modal content goes here.</p>
          <button onClick={closeModal}>Close Modal</button>
      </Modal>
    </td>
  )
}

export default ActionButton
