import React, { useState } from 'react'
import { Link } from 'react-router-dom'

const ActionButton = ({id, sourceName}) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };
  return (
    <td>
        <Link to={`/admin/mera-bazzar/edit-${sourceName}/${id}`} className="action-icon"> <i className="mdi mdi-square-edit-outline"></i></Link>
        <button onClick={openModal} className="action-icon"> <i className="mdi mdi-delete"></i></button>

        {modalIsOpen && (
           <div id="danger-alert-modal" class="modal fade" tabindex="-1" role="dialog" aria-hidden="true">
                <div class="modal-dialog modal-sm">
                    <div class="modal-content modal-filled bg-danger">
                        <div class="modal-body p-4">
                            <div class="text-center">
                                <i class="dripicons-wrong h1 text-white"></i>
                                <h4 class="mt-2 text-white">Oh snap!</h4>
                                <p class="mt-3 text-white">Cras mattis consectetur purus sit amet fermentum. Cras justo odio, dapibus ac facilisis in, egestas eget quam.</p>
                                <button type="button" class="btn btn-light my-2" data-dismiss="modal">Continue</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )}

    </td>
  )
}

export default ActionButton
