import React, { useState } from "react";
import "./style.scss";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../../features/auth/authSlice";

interface LoginCredentials {
    email: string;
    password: string;
  }

const Login = () => {

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [formData, setFormData] = useState<LoginCredentials>({ // Use the new type here
        email: '',
        password: ''
      });
      const [errors, setErrors] = useState({
        email: '',
        password: ''
      });

  const authState = useSelector((state) => state);

  const { user, isError, isSuccess, isLoading, message } = authState.auth;

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));

    // Validate email
    if (name === 'email') {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      setErrors(prevState => ({
        ...prevState,
        email: !emailRegex.test(value) ? 'Invalid email format' : ''
      }));
    }

    // Validate password
    if (name === 'password') {
      setErrors(prevState => ({
        ...prevState,
        password: value.length < 6 ? 'Password must be at least 6 characters long' : ''
      }));
    }
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    // Check if there are any errors before dispatching login action
    if (!errors.email && !errors.password) {
      dispatch(login({ email: formData.email, password: formData.password }));
    }
  };

  return (
    <div className="authentication-bg authentication-bg-pattern">
      <div className="account-pages mt-5 mb-5">
        <div className="container">
          <div className="row justify-content-center">
            <div className="col-md-8 col-lg-6 col-xl-5">
              <div className="card bg-pattern">
                <div className="card-body p-4">
                  <div className="text-center w-75 m-auto">
                    <div className="auth-logo">
                      <Link to="/" className="logo logo-dark text-center">
                        <span className="logo-lg">
                          <img
                            src="../assets/images/logo-dark.png"
                            alt=""
                            height="22"
                          />
                        </span>
                      </Link>

                      <Link to="/" className="logo logo-light text-center">
                        <span className="logo-lg">
                          <img
                            src="../assets/images/logo-light.png"
                            alt=""
                            height="22"
                          />
                        </span>
                      </Link>
                    </div>
                    <p className="text-muted mb-4 mt-3">
                      Enter your email address and password to access admin
                      panel.
                    </p>
                  </div>

                  <form onSubmit={handleSubmit}>
                    <div className="form-group mb-3">
                      <label htmlFor="emailaddress">Email address</label>
                      <input
                        className={`form-control ${
                          errors.email ? "is-invalid" : ""
                        }`}
                        type="email"
                        id="emailaddress"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                        placeholder="Enter your email"
                      />
                      {errors.email && (
                        <div className="invalid-feedback">{errors.email}</div>
                      )}
                    </div>

                    <div className="form-group mb-3">
                      <label htmlFor="password">Password</label>
                      <div className="input-group input-group-merge">
                        <input
                          type="password"
                          id="password"
                          className={`form-control ${
                            errors.password ? "is-invalid" : ""
                          }`}
                          name="password"
                          value={formData.password}
                          onChange={handleChange}
                          placeholder="Enter your password"
                        />
                        <div
                          className="input-group-append"
                          data-password="false"
                        >
                          <div className="input-group-text">
                            <span className="password-eye"></span>
                          </div>
                        </div>
                        {errors.password && (
                          <div className="invalid-feedback">
                            {errors.password}
                          </div>
                        )}
                      </div>
                    </div>

                    <div className="form-group mb-3">
                      <div className="custom-control custom-checkbox">
                        <input
                          type="checkbox"
                          className="custom-control-input"
                          id="checkbox-signin"
                          checked // You may want to bind this to state
                        />
                        <label
                          className="custom-control-label"
                          htmlFor="checkbox-signin"
                        >
                          Remember me
                        </label>
                      </div>
                    </div>

                    <div className="form-group mb-0 text-center">
                      <button
                        className="btn btn-primary btn-block"
                        type="submit"
                        disabled={isLoading} // Disable button while loading
                      >
                        {isLoading ? "Logging In..." : "Log In"}
                      </button>
                    </div>
                    {isError && <div>Error: Failed to log in</div>}
                  </form>

                  <div className="text-center">
                    <h5 className="mt-3 text-muted">Sign in with</h5>
                    <ul className="social-list list-inline mt-3 mb-0">
                      <li className="list-inline-item">
                        <Link
                          to="#"
                          className="social-list-item border-primary text-primary"
                        >
                          <i className="mdi mdi-facebook"></i>
                        </Link>
                      </li>
                      <li className="list-inline-item">
                        <Link
                          to="#"
                          className="social-list-item border-danger text-danger"
                        >
                          <i className="mdi mdi-google"></i>
                        </Link>
                      </li>
                      <li className="list-inline-item">
                        <Link
                          to="#"
                          className="social-list-item border-info text-info"
                        >
                          <i className="mdi mdi-twitter"></i>
                        </Link>
                      </li>
                      <li className="list-inline-item">
                        <Link
                          to="#"
                          className="social-list-item border-secondary text-secondary"
                        >
                          <i className="mdi mdi-github"></i>
                        </Link>
                      </li>
                    </ul>
                  </div>
                </div>
              </div>

              <div className="row mt-3">
                <div className="col-12 text-center">
                  <p>
                    {" "}
                    <Link
                      to="auth-recoverpw.html"
                      className="text-white-50 ml-1"
                    >
                      Forgot your password?
                    </Link>
                  </p>
                  <p className="text-white-50">
                    Don't have an account?{" "}
                    <Link to="auth-register.html" className="text-white ml-1">
                      <b>Sign Up</b>
                    </Link>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <footer className="footer footer-alt">
        2023 - {new Date().getFullYear()} &copy; Admin Dashboard by{" "}
        <Link to="#">Shas Legacy</Link>
      </footer>
    </div>
  );
};

export default Login;
