import React from 'react'
import Bredcrumb from 'src/components/core/Bredcrumb'
import { useFormik } from 'formik';
import ReactQuill from 'react-quill';
import * as yup from 'yup';
import './styles.scss'
import 'react-quill/dist/quill.snow.css';
import SelectResource from 'src/components/core/SelectResource';

let schema = yup.object().shape({
    title: yup.string().required("Title is Required"),
    description: yup.string().required("Description is Required"),
    price: yup.number().required("Price is Required"),
    brand: yup.string().required("Brand is Required"),
    category: yup.string().required("Category is Required"),
    tags: yup.string().required("Tag is Required"),
    color: yup
      .array()
      .min(1, "Pick at least one color")
      .required("Color is Required"),
    quantity: yup.number().required("Quantity is Required"),
  });

const AddProduct = () => {

    const formik = useFormik({
        initialValues: {
          title: "",
          description: "",
          price: "",
          brand: "",
          category: "",
          tags: "",
          color: "",
          quantity: "",
          images: "",
        },
        validationSchema: schema,
        onSubmit: (values) => {
        //   dispatch(createProducts(values));
        //   formik.resetForm();
        //   setColor(null);
        //   setTimeout(() => {
        //     dispatch(resetState());
        //   }, 3000);
        },
      });

  return (
    <div className="content">
        <div className="container-fluid">     

            <Bredcrumb title="Add Product" />

            <form onSubmit={formik.handleSubmit}>
                <div className="row">
                    <div className="col-lg-6">
                        <div className="card-box">
                            <h5 className="text-uppercase bg-light p-2 mt-0 mb-3">General</h5>

                            <div className="form-group mb-3">
                                <label htmlFor="product-name">Product Name <span className="text-danger">*</span></label>
                                <input type="text"
                                    id="product-name"
                                    className="form-control"
                                    name='title'
                                    placeholder="e.g : Apple iMac"
                                    onChange={formik.handleChange("title")}
                                    onBlur={formik.handleBlur("title")}
                                    value={formik.values.title}
                                     />
                                    {formik.touched.title && formik.errors.title ? (
                                      <div className="text-danger mt-1 error-text">{formik.errors.title}</div>
                                    ) : null}
                            </div>

                            <div className="form-group mb-3">
                                <label htmlFor="product-description">Product Description <span className="text-danger">*</span></label>
                                <ReactQuill 
                                 theme="snow"
                                 id="product-description" 
                                 rows="5" 
                                 name='description'
                                 placeholder="Please enter description"
                                 onChange={formik.handleChange("description")}
                                 value={formik.values.description}
                                  />
                                {formik.touched.description && formik.errors.description ? (
                                      <div className="text-danger mt-1 error-text">{formik.errors.description}</div>
                                    ) : null}
                            </div>

                            <div className="form-group mb-3">
                                <SelectResource title="Brand" name="brand" id ="product-brand" />
                                {formik.touched.brand && formik.errors.brand ? (
                                      <div className="text-danger mt-1 error-text">{formik.errors.brand}</div>
                                    ) : null}
                            </div>

                            <div className="form-group mb-3">
                                <SelectResource title="Category" name="category" id ="product-category" />
                                {formik.touched.category && formik.errors.category ? (
                                      <div className="text-danger mt-1 error-text">{formik.errors.category}</div>
                                    ) : null}
                            </div>
                        </div> 
                    </div> 

                    <div className="col-lg-6">

                        <div className="card-box">
                            <h5 className="text-uppercase mt-0 mb-3 bg-light p-2">Other Information</h5>
                            <div className="form-group mb-3">
                                <label htmlFor="product-images">Product Images</label>
                                <input type="file" className="form-control" id="product-images" placeholder="Enter title" />
                            </div>

                            <div className="form-group mb-3">
                                <label htmlFor="product-price">Price <span className="text-danger">*</span></label>
                                <input
                                 type="text"
                                 className="form-control"
                                 id="product-price"
                                 name='price'
                                 onChange={formik.handleChange("price")}
                                 onBlur={formik.handleBlur("price")}
                                 value={formik.values.price}
                                 placeholder="Enter amount" />
                                {formik.touched.price && formik.errors.price ? (
                                      <div className="text-danger mt-1 error-text">{formik.errors.price}</div>
                                ): null}
                            </div>

                            <div className="form-group mb-3">
                                <label htmlFor="product-color">Colors</label>
                                <SelectResource title="Colors" name="color" id ="product-color" multiple />
                                {formik.touched.color && formik.errors.color ? (
                                      <div className="text-danger mt-1 error-text">{formik.errors.color}</div>
                                  ): null}
                            </div>

                            <div className="form-group mb-3">
                                <label htmlFor="product-quantity">Quantity</label>
                                <input 
                                 type="number"
                                 className="form-control"
                                 id="product-quantity"
                                 placeholder="Enter quantity"
                                 name='quantity'
                                 onChange={formik.handleChange("quantity")}
                                 onBlur={formik.handleBlur("quantity")}
                                 value={formik.values.quantity}
                                  />
                                  {formik.touched.quantity && formik.errors.quantity ? (
                                      <div className="text-danger mt-1 error-text">{formik.errors.quantity}</div>
                                  ): null}
                            </div>

                            <div className="form-group mb-0">
                                <label htmlFor="product-tags">Tags </label>
                                <SelectResource title="Tags" name="tags" id ="product-tags" />
                                {formik.touched.tags && formik.errors.tags ? (
                                      <div className="text-danger mt-1 error-text">{formik.errors.tags}</div>
                                  ): null}
                            </div>
                        </div> 

                    </div> 
                </div>


                <div className="row">
                    <div className="col-12">
                        <div className="text-center mb-3">
                            <button type="button" className="btn w-sm btn-light waves-effect m-1">Cancel</button>
                            <button type="submit" className="btn w-sm btn-success waves-effect waves-light m-1">Save</button>
                            <button type="button" className="btn w-sm btn-danger waves-effect waves-light">Delete</button>
                        </div>
                    </div> 
                </div>

            </form>

        </div> 
    </div>
  )
}

export default AddProduct
