import React from "react";
import LocaleData from "../../../../../data/locale.json";

const Locale = () => {
  return (
    <li className="dropdown d-none d-lg-inline-block topbar-dropdown">
      <a
        className="nav-link dropdown-toggle arrow-none waves-effect waves-light"
        href="#"  // Use href="#" instead of "javascript:void(0);"
        role="button"
        aria-haspopup="false"
        aria-expanded="false"
      >
        <img src="../assets/images/flags/us.jpg" alt="flag-icon" height="16" />
      </a>
      <div className="dropdown-menu dropdown-menu-right">
        {/*  item */}
        {LocaleData.map((data: any) => (
          <a
            href="#"
            className="dropdown-item"
            key={data.id}
            role="menuitem"  // Add role for accessibility
          >
            <img
              src={`../assets/images/flags/${data.image}`}
              alt="flag-icon"  // Use a more descriptive alt text
              className="mr-1"
              height="12"
            />
            <span className="align-middle">{data.name}</span>
          </a>
        ))}
      </div>
    </li>
  );
};

export default Locale;
