import { createSlice, createAsyncThunk, PayloadAction } from "@reduxjs/toolkit";
import authService from "./authService";

interface User {
  _id: string;
  firstname: string;
  lastname: string;
  email: string;
  mobile: string;
  token: string;
  password: string;
}

interface AuthState {
  user: User;
  isError: boolean;
  isLoading: boolean;
  isSuccess: boolean;
  message: string;
}

const userDefaultState: User = {
  _id: "",
  firstname: "",
  lastname: "",
  email: "",
  mobile: "",
  token: "",
  password: "",
};

const initialState: AuthState = {
  user: userDefaultState,
  isError: false,
  isLoading: false,
  isSuccess: false,
  message: "",
};

export const login = createAsyncThunk<User, User, { rejectValue: any }>(
  "auth/admin-login",
  async (user: User, thunkAPI: any) => {
    try {
      const response = await authService.login(user);
      return response;
    } catch (error) {
      return thunkAPI.rejectWithValue(error);
    }
  }
);

export const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(login.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(login.fulfilled, (state, action: PayloadAction<User>) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.user = action.payload;
      })
      .addCase(login.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.error ? action.error.message || "An error occurred" : "An error occurred";
        state.user = userDefaultState;
      });
  },
});

export default authSlice.reducer;
